from django.shortcuts import renderfrom django.views import Viewfrom django.utils.translation import gettext_lazy as _from django.contrib.auth.views import LoginView, LogoutViewfrom django.urls import reverse_lazyfrom django.contrib.messages.views import SuccessMessageMixinfrom django.contrib import messagesclass StartPageView(View):    @staticmethod    def get(request, *args, **kwargs):        return render(request, 'start_page.html', {})class CustomLoginView(SuccessMessageMixin, LoginView):    template_name = 'login.html'    next_page = reverse_lazy('start_page')    success_message = _('You are logged in successfully!')class CustomLogoutView(LogoutView):    message = _('You are logged out')    def dispatch(self, request, *args, **kwargs):        messages.info(request, message=self.message)        return super().dispatch(request, *args, **kwargs)